  inset: 0;
  margin: auto;
  transition: opacity 1000ms 200ms ease-in-out, transform 200ms ease-in-out;
}

.button:active::before {
  transform: scale(13);
  opacity: 1;
  transition: all 400ms ease-in-out;
}

.button:has(:first-child:active)::before {
  margin-left: 0;
}

.button:has(:last-child:active)::before {
  margin-right: 0;
}

