.button::after {
  content: "";
  position: absolute;
  width: 100%;
  height: 2em;
  border: .1em solid black;
  bottom: 0;
  left: -.15em;
  transform-origin: bottom;
  transform: rotateX(90deg);
  transition: all 150ms;
  background-color: rgb(199, 179, 155);
}

.button:active {
  transform: rotateX(40deg)rotateZ(40deg) translateZ(-1em);
}

.button:active::before {
  width: 0;
}

.button:active::after {
  height: 0;
}

