/* From Uiverse.io by FoxVidaan - Tags: card */
.card {
  max-width: 300px;
  font-family: sans-serif;
  color: white;
  background: linear-gradient(-45deg, #7121A6, #520153, #044146, #04967b );
  background-size: 400% 400%;
  animation: gradient 12s ease infinite;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  border-radius: 10px;
}

.heading {
  position: relative;
  overflow: hidden;
  font-size: 1.2rem;
  font-weight: 600;
  text-align: center;
  padding-bottom: 10px;
}

.heading span {
  display: block;
  margin-top: -0.5rem;
  font-size: 2.7rem;
  font-weight: 800;
  -webkit-text-stroke: #fff 0.1rem;
  letter-spacing: 0.2rem;
  color: transparent;
  position: relative;
  /* top: 10rem; */
  /* animation: slide-in 0.4s 1s both; */
  text-shadow: 0px 0px 16px #CECECE;
}

.content {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.8rem;
}

.content .item {
  width: 110px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 10px 16px;
  background: rgba( 255, 255, 255, 0.05 );
  box-shadow: 0 0 2px 2px rgba(255, 255, 255, 0.37);
  backdrop-filter: blur( 3px );
  -webkit-backdrop-filter: blur( 3px );
  border-radius: 10px;
}

.content .item:first-child {
  align-self: flex-start;
}

.content .item:last-child {
  align-self: flex-end;
}

.content .item.item--create:hover svg {
  animation: create-animation 1s infinite alternate;
}

.content .item.item--post:hover svg {
  animation: post-animation 2s infinite;
}

.content .item.item--inspire:hover svg {
  animation: inspire-animation 2s infinite;
}

button {
  cursor: pointer;
  margin-top: 0.5rem;
  font-weight: 800;
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 0.1rem;
  color: #7121a6af;
  animation: text-color 12s ease infinite;
  background-color: #ffffffbb;
  border: none;
  border-radius: 20px;
  padding: 16px 24px;
  transition: 0.4s;
  box-shadow: -4px -2px 10px 0px #ffffffbb, 4px 2px 16px 0px #02735e80;
}

button:hover {
  color: #7121a6;
  background-color: #ffffff;
  box-shadow: -2px -1px 8px 0px #ffffff, 2px 1px 8px 0px #02735e;
}

button:active {
  box-shadow: none;
}

@keyframes gradient {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

@keyframes slide-in {
  100% {
    top: 0rem;
  }
}

@keyframes create-animation {
  0% {
    transform: scaleX(0.8);
  }

  100% {
    transform: scaleX(1.2);
  }
}

@keyframes post-animation {
  0%, 100% {
    transform: rotate(-90deg) translateY(0%);
  }

  45% {
    transform: rotate(-90deg) translateY(-50%);
  }

  50% {
    transform: rotate(-90deg) translateY(-50%) scaleY(-1);
  }

  55% {
    transform: rotate(-90deg) translateY(-50%) scaleY(-1);
  }

  90% {
    transform: rotate(-90deg) translateY(0%) scaleY(-1);
  }
}

@keyframes inspire-animation {
  0%,60% {
    filter: brightness(0.8) drop-shadow(0px 0px 2px rgb(0 0 0 / 0.4));
  }

  30%,100% {
    filter: brightness(1) drop-shadow(0px 0px 2px rgba(255, 255, 255, 0.4));
  }
}

@keyframes text-color {
  0% {
    color: #02735E;
  }

  50% {
    color: #7121a6;
  }

  100% {
    color: #02735E;
  }
}

