/* From Uiverse.io by G4b413l - Tags: glassmorphism, card, hover */
.card {
  width: 190px;
  height: 254px;
  transition: all 0.2s;
  position: relative;
  cursor: pointer;
}

.card-inner {
  width: inherit;
