/* From Uiverse.io by Kemboi-Dun - Tags: card */
.container {
  width: 250px;
  height: 250px;
}

.terminal_toolbar {
  display: flex;
  height: 30px;
  align-items: center;
  padding: 0 8px;
  box-sizing: border-box;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  background: #212121;
  justify-content: space-between;
}

.butt {
  display: flex;
  align-items: center;
}

.btn {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0;
  margin-right: 5px;
  font-size: 8px;
  height: 12px;
  width: 12px;
  box-sizing: border-box;
  border: none;
  border-radius: 100%;
  background: linear-gradient(#7d7871 0%, #595953 100%);
  text-shadow: 0px 1px 0px rgba(255, 255, 255, 0.2);
  box-shadow: 0px 0px 1px 0px #41403A, 0px 1px 1px 0px #474642;
}

.btn-color {
  background: #ee411a;
}

.btn:hover {
  cursor: pointer;
}

.btn:focus {
  outline: none;
}

.butt--exit {
  background: linear-gradient(#f37458 0%, #de4c12 100%);
}

.add_tab {
  border: 1px solid #fff;
  color: #fff;
  padding: 0 6px;
  border-radius: 4px 4px 0 0;
  border-bottom: none;
  cursor: pointer;
}

.user {
  color: #d5d0ce;
  margin-left: 6px;
  font-size: 14px;
  line-height: 15px;
}

.terminal_body {
  background: rgba(0, 0, 0, 0.6);
  height: calc(100% - 30px);
  padding-top: 2px;
  margin-top: -1px;
  font-size: 12px;
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
}

.terminal_promt {
  display: flex;
}

.terminal_promt span {
  margin-left: 4px;
}

.terminal_user {
  color: #1eff8e;
}

.terminal_location {
  color: #4878c0;
}

.terminal_bling {
  color: #dddddd;
}

.terminal_cursor {
  display: block;
  height: 14px;
  width: 5px;
  margin-left: 10px;
  animation: curbl 1200ms linear infinite;
}

@keyframes curbl {

  0% {
    background: #ffffff;
  }

  49% {
    background: #ffffff;
  }

  60% {
    background: transparent;
  }

  99% {
    background: transparent;
  }

  100% {
    background: #ffffff;
  }
}

/* From Uiverse.io by KhaledMatalkah - Tags: animation, card, hover, cube */
.cube-container {
  width: 200px;
  height: 200px;
  perspective: 800px;
  margin: 50px auto;
}

.cube {
  position: relative;
  width: 100%;
  height: 100%;
  transform-style: preserve-3d;
  animation: rotate 8s infinite linear;
}

.face {
  position: absolute;
  width: 200px;
  height: 200px;
  color: rgb(214, 21, 21);
  font-size: 18px;
  text-align: center;
  line-height: 200px;
  background: transparent;
  opacity: 0.9;
  border: 2px solid;
  border-image: linear-gradient(to right, #ff6b6b, #355c7d, #557d35, #cfcf16, #a51f1f) 1;
  box-shadow: 0 0 100px rgba(245, 8, 8, 0.8);
}

.front {
  transform: translateZ(100px);
}

.back {
  transform: rotateY(180deg) translateZ(100px);
}

.right {
  transform: rotateY(90deg) translateZ(100px);
}

.left {
  transform: rotateY(-90deg) translateZ(100px);
}

.top {
  transform: rotateX(90deg) translateZ(100px);
}

.bottom {
  transform: rotateX(-90deg) translateZ(100px);
}

.cube-container:hover .cube {
  animation-play-state: paused;
}

@keyframes rotate {
  0% {
    transform: rotateX(0) rotateY(0) rotateZ(0);
  }

  100% {
    transform: rotateX(360deg) rotateY(360deg) rotateZ(360deg);
  }
}

/* From Uiverse.io by KhaledMatalkah - Tags: card, hover, animated */
.card {
  width: 254px;
  height: 300px;
  background: linear-gradient(180deg, #5b1577, #a52585);
  border-radius: 10%;
  box-shadow: -35px 35px 70px #891883, 10px -35px 70px #db26db;
  position: relative;
  overflow: hidden;
  transition: transform 0.3s ease;
  animation: shadowAnimation 5s infinite ease-in-out;
}

@keyframes shadowAnimation {
  0% {
    box-shadow: -35px 35px 70px #ca21ae, 10px -35px 70px #ca21ae;
  }

  50% {
    box-shadow: -20px 10px 50px #bd3fa2, 0 -20px 50px #b9317c;
  }

  100% {
    box-shadow: -35px 35px 70px #ca27af, 10px -35px 70px #bd3fa2;
  }
}

.card::before {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 150px;
  height: 150px;
  background: radial-gradient(circle, #68057c, #a52574);
  border-radius: 50%;
  opacity: 0.6;
  transition: opacity 0.3s ease;
}

.card::after {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80px;
  height: 80px;
  background: radial-gradient(circle, #b700ff, #be268b, transparent);
  border-radius: 50%;
  opacity: 0.2;
  transition: opacity 0.3s ease;
}

.card:hover {
  transform: scale(1.1);
}

.card:hover::before,
.card:hover::after {
  opacity: 1;
}

/* From Uiverse.io by KillerRP - Tags: 3d, clock, animated */
.one-div {
  --box-width: 100px;
  width: var(--box-width);
  height: var(--box-width);
  border: 2px solid red;
  background-color: white;
  border-radius: 100%;
  position: relative;
  transform: scale(1);
  transition: 2s ease;
  cursor: pointer;
  box-shadow: 2px 2px 10px white;
}

.one-div:hover {
  transform: scale(1.5);
  box-shadow: 2px 2px 50px yellow;
}

.one-div::before, .one-div::after {
  content: "";
  width: 2px;
  display: block;
  height: 100%;
  background: linear-gradient(black 50%, transparent 50%);
  border-radius: 20px;
  position: absolute;
  left: 50%;
  transform-origin: translate(-100%, -100%);
}

.one-div::after {
  height: 100%;
  animation: secondsMotion 120s  infinite linear;
}

.one-div::before {
  animation: secondsMotion 6s infinite linear;
}

@keyframes secondsMotion {
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(360deg);
  }
}

