.card span::before {
  content: "";
  position: absolute;
  top: 120px;
  left: 176px;
  transform: rotate(90deg);
  bottom: -5px;
  width: 30px;
  height: 10px;
  background: #53ef7d;
  border-radius: 10px;
  transition: 0.5s;
  transition-delay: 0.5;
}

.card span:nth-child(2)::before {
  left: -5px;
}

.card span:nth-child(2)::after {
  top: 2px;
  left: 75px;
}

.card:hover span:nth-child(2)::before {
  animation-delay: .1s;
  animation: drop 1s linear infinite;
}

.card:hover .content:hover span:nth-child(2)::before {
  display: none;
}

.card:hover span:nth-child(2)::after {
  animation-delay: .2s;
  animation: drop 0.5s linear infinite;
}

.card:hover .content:hover span:nth-child(2)::after {
  display: none;
}

@keyframes drop {
  0% {
    top: -10px;
    transform: translateY(0);
  }

  100% {
    top: -10px;
    transform: translateY(500px);
  }
}

.card :hover span::before {
  left: 90px;
