}

.item:hover {
  cursor: pointer;
  scale: 0.9;
  box-shadow: none;
}

.item:active {
  cursor: pointer;
  scale: 0.8;
  box-shadow: inset 2px 5px 10px rgba(0,0,0,0.2);
}

.item--create {
  padding-left: 2.5em;
