  left: 0;
  top: 115px;
}

@keyframes LineFadeIn {
  0% {
    opacity: 0;
    d: path("M 0 300 Q 0 300 0 300 Q 0 300 0 300 C 0 300 0 300 0 300 Q 0 300 0 300 ");
    stroke: #fff;
  }

  50% {
    opacity: 1;
    d: path("M 0 300 Q 50 300 100 300 Q 250 300 350 300 C 350 300 500 300 650 300 Q 750 300 800 300");
    stroke: #888BFF;
  }

  100% {
    opacity: 1;
    d: path("M -2 100 Q 50 200 100 250 Q 250 400 350 300 C 400 250 550 150 650 300 Q 750 450 802 400");
    stroke: #545581;
  }
}

@keyframes ContentFadeIn {
  0% {
    transform: translateY(-1rem);
    opacity: 0;
  }

  100% {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes ImageFadeIn {
  0% {
    transform: translate(-.5rem, -.5rem) scale(1.05);
    opacity: 0;
    filter: blur(2px);
  }

  50% {
    opacity: 1;
    filter: blur(2px);
  }

  100% {
    transform: translateY(0) scale(1.0);
    opacity: 1;
    filter: blur(0);
  }
}

/* From Uiverse.io by Shohjahon3807 - Tags: card, hover, follow me */
.one-div {
  width: 300px;
  height: 200px;
  background-color: #f0f5f9;
  border-radius: 10px;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.15);
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  font-family: Arial, sans-serif;
  font-size: 24px;
  color: #333;
  position: relative;
  overflow: hidden;
}

.one-div::before {
  content: "";
  position: absolute;
  top: -10px;
  left: -10px;
  right: -10px;
  bottom: -10px;
  z-index: -1;
  background-image: linear-gradient(45deg, #e1e8f2, #f0f5f9);
  border-radius: 20px;
  opacity: 0.8;
  transform: scale(1.2);
  filter: blur(10px);
}

.one-div:hover {
  transform: scale(1.05);
  transition: transform 0.3s ease;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
}

.one-div:hover::before {
  animation: gradient-animation 5s linear infinite;
}

@keyframes gradient-animation {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

/* From Uiverse.io by ShrinilDhorda - Tags: card, glow, code, blur filter, gradients, svg, clean, creative, challenge */
.card {
  padding: 4.4rem 1.4rem;
  display: flex;
  gap: 1.5rem;
  flex-direction: column;
  position: relative;
  overflow: hidden;
  border-radius: 1rem;
}

.card::before {
  content: '';
  width: 5rem;
  height: 5rem;
  border-radius: 100%;
  position: absolute;
  top: 0%;
  left: 0%;
  background-color: rgb(163, 159, 198);
  filter: blur(50px) saturate(5);
  animation: top 8s infinite ease-in-out;
}

.card::after {
  content: '';
  width: 5rem;
  height: 5rem;
  border-radius: 100%;
  position: absolute;
  bottom: 0%;
  left: 85%;
  background-color: rgba(163, 159, 198);
  filter: blur(50px) saturate(5);
  animation: bottom 8s infinite ease-in-out;
}

.card .heading {
  display: flex;
  flex-direction: column;
  font-size: 1.3rem;
  justify-content: center;
  align-items: center;
  user-select: none;
}

.card .heading span {
  font-size: 3rem;
  text-transform: uppercase;
  letter-spacing: 0.4rem;
  text-shadow: 0 0 19px rgba(255, 255, 255, 0.695), 0 0 20px rgba(255, 255, 255, 0.568), 0 0 30px #1a0e34, 0 0 40px #721faad9, 0 0 50px #2c26729f, 0 0 60px #180e5c5c, 0 0 70px #101d3548;
  border-radius: 70%;
  padding: 0.5rem;
  position: relative;
}

.card .content {
  display: flex;
}

.card .content .item {
  padding: 0.7rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
  opacity: 70%;
  transition: 0.5s ease;
  margin: 0 0.5rem;
  position: relative;
  border-radius: 2rem;
}

.item::before {
  content: '';
  width: 1.5rem;
  height: 1.5rem;
  border-radius: 100%;
  background-color: rgba(255, 255, 255, 0.466);
  position: absolute;
  filter: blur(20px);
  top: 0%;
}

button {
  background-color: transparent;
  font-size: 1.3rem;
  padding: 1rem;
  color: rgba(255, 255, 255, 0.948);
  transition: 0.5s ease-in-out;
  cursor: pointer;
  position: relative;
  border: none;
  border-radius: 2rem;
  box-shadow: 0px 10px 70px 33px rgba(0,0,0,0.1);
  background: linear-gradient(90deg, rgba(221, 218, 222, 0.185) 20%, rgba(16, 9, 9, 0.311) 100%);
  background-size: 400% 400%;
  animation: gradient 13s ease infinite;
}

button::before {
  content: '';
  width: 10rem;
  height: 2.5rem;
  border-radius: 100%;
  background-color: rgb(163, 159, 198,0.3);
  position: absolute;
  filter: blur(25px);
  top: 10%;
}

button:hover {
  scale: 102%;
}

@keyframes gradient {
  0% {
    background-position: 0% 50%;
    transform: rotateY(10deg);
  }

  50% {
    background-position: 100% 50%;
    transform: rotateY(13deg);
  }

  100% {
    background-position: 0% 50%;
    transform: rotateY(10deg);
  }
}

@keyframes top {
  0% {
    top: 0%;
    transform: rotate(0deg);
  }

  50% {
    top: 10%;
    transform: rotate(180deg);
  }

  100% {
    top: 0%;
    transform: rotate(0deg);
  }
}

@keyframes bottom {
  0% {
    bottom: 0%;
    transform: rotate(0deg);
  }

  50% {
    bottom: 10%;
    transform: rotate(180deg);
  }

  100% {
    bottom: 0%;
    transform: rotate(0deg);
  }
}

.card .content .item:hover {
  opacity: 100%;
  transform: translateY(-5px);
}

/* From Uiverse.io by Smit-Prajapati - Tags: card, sci-fi, cool card, cardhover , card animation, card hover, card design */
.card {
  position: relative;
  height: 300px;
  width: 230px;
}

.card .boxshadow {
  position: absolute;
  height: 100%;
  width: 100%;
  border: 1px solid red;
  transform: scale(0.8);
  box-shadow: rgb(255, 0, 0) 0px 30px 70px 0px;
  transition: all 0.5s cubic-bezier(0.785, 0.135, 0.15, 0.86);
}

.card .main {
  width: 100%;
  height: 100%;
  overflow: hidden;
  background: red;
  background: linear-gradient(0deg, rgb(62, 0, 0) 0%, rgb(255, 0, 0) 60%, rgb(62, 0, 0) 100%);
  position: relative;
  -webkit-clip-path: polygon(0 40px, 40px 0, calc(100% - 40px) 0, 100% 40px, 100% calc(100% - 40px), calc(100% - 40px) 100%, 40px 100%, 0 calc(100% - 40px));
  clip-path: polygon(0 40px, 40px 0, calc(100% - 40px) 0, 100% 40px, 100% calc(100% - 40px), calc(100% - 40px) 100%, 40px 100%, 0 calc(100% - 40px));
  -webkit-clip-path: polygon(0 0, 100% 0, 100% 40px, 100% calc(100% - 40px), calc(100% - 40px) 100%, 40px 100%, 0 calc(100% - 40px));
  clip-path: polygon(0 0, 100% 0, 100% 40px, 100% calc(100% - 40px), calc(100% - 40px) 100%, 40px 100%, 0 calc(100% - 40px));
  box-shadow: rgb(255, 0, 0) 0px 7px 29px 0px;
  transition: all 0.3s cubic-bezier(0.785, 0.135, 0.15, 0.86);
}

.card .main .top {
  position: absolute;
  top: 0px;
  left: 0;
  width: 0px;
  height: 0px;
  z-index: 2;
  border-top: 115px solid black;
  border-left: 115px solid transparent;
  border-right: 115px solid transparent;
  transition: all 0.5s cubic-bezier(0.785, 0.135, 0.15, 0.86);
}

.card .main .side {
  position: absolute;
  width: 100%;
  top: 0;
  transform: translateX(-50%);
  height: 101%;
  background: black;
  -webkit-clip-path: polygon(0% 0%, 50% 0, 95% 45%, 100% 100%, 0% 100%);
  clip-path: polygon(0% 0%, 50% 0, 95% 45%, 100% 100%, 0% 100%);
  transition: all 0.5s cubic-bezier(0.785, 0.135, 0.15, 0.86) 1s;
}

.card .main .left {
  left: 0;
}

.card .main .right {
  right: 0;
  transform: translateX(50%) scale(-1, 1);
}

.card .main .title {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  top: 90px;
  font-weight: bold;
  font-size: 25px;
  opacity: 0;
  z-index: -1;
  transition: all 0.2s ease-out 0s;
}

.card .main .button-container {
  position: absolute;
  bottom: 10px;
  left: 50%;
  transform: translateX(-50%);
}

.card .main .button-container .button {
  position: absolute;
  transform: translateX(-50%);
  padding: 5px 10px;
  -webkit-clip-path: polygon(0 0, 100% 0, 81% 100%, 21% 100%);
  clip-path: polygon(0 0, 100% 0, 81% 100%, 21% 100%);
  background: black;
  border: none;
  color: white;
  display: grid;
  place-content: center;
  transition: all 0.5s cubic-bezier(0.785, 0.135, 0.15, 0.86);
}

.card .main .button-container .button .svg {
  width: 15px;
  transition: all 0.2s cubic-bezier(0.785, 0.135, 0.15, 0.86);
}

.card .main .button-container .button:nth-child(1) {
  bottom: 300px;
  transition-delay: 0.4s;
}

.card .main .button-container .button:nth-child(2) {
  bottom: 300px;
  transition-delay: 0.6s;
}

.card .main .button-container .button:nth-child(3) {
  bottom: 300px;
  transition-delay: 0.8s;
}

.card .main .button-container .button:hover .svg {
  transform: scale(1.2);
}

.card .main .button-container .button:active .svg {
  transform: scale(0.7);
}

.card:hover .main {
  transform: scale(1.1);
}

.card:hover .main .top {
  top: -50px;
}

.card:hover .main .left {
  left: -50px;
  transition: all 0.5s cubic-bezier(0.785, 0.135, 0.15, 0.86) 0.1s;
}

.card:hover .main .right {
  right: -50px;
  transition: all 0.5s cubic-bezier(0.785, 0.135, 0.15, 0.86) 0.1s;
}

.card:hover .main .title {
  opacity: 1;
  transition: all 0.2s ease-out 1.3s;
}

.card:hover .main .button-container .button:nth-child(1) {
  bottom: 80px;
  transition-delay: 0.8s;
}

.card:hover .main .button-container .button:nth-child(2) {
  bottom: 40px;
  transition-delay: 0.6s;
}

.card:hover .main .button-container .button:nth-child(3) {
  bottom: 0;
  transition-delay: 0.4s;
}

