}

.card .item {
  background-color: rgba(23, 19, 26,.8);
  margin: 10px 0;
  padding: 5px 15px;
  position: relative;
}

.card .item::before {
  content: " ";
  position: absolute;
  display: inline-block;
  width: 0px;
  height: 30%;
  left: -10px;
  border: 10px solid rgb(84, 76, 90);
  top: -5px;
}

.card .item::after {
  content: " ";
  position: absolute;
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 0 7px 7px 7px;
  border-color: transparent transparent rgb(0, 0, 0) transparent;
  transform: rotate(-45deg);
  left: -5px;
  bottom: 3px;
}

.card .item:hover {
  transform: scale(1.01);
  background-color: grey;
}

.card .item svg {
  vertical-align: middle;
  margin-right: 10px;
}

.card:active:before {
  content: "";
  position: absolute;
  top: -10px;
  left: -10px;
  right: -10px;
  bottom: -10px;
  z-index: -1;
  border-radius: 5px;
  background: rgba(0, 0, 0, 0.1);
  animation: ripple 5s linear;
}

.card:hover {
  animation: shake 0.5s linear;
