  0% {
    transform: rotate(10deg) translate(20px);
  }

  25% {
    transform: rotate(-10deg) translate(-20px);
  }

  50% {
    transform: rotate(5deg) translate(20px);
  }

  75% {
    transform: rotate(-5deg) translate(-20px);
  }

  100% {
    transform: rotate(0deg) translate(0px);
  }
}

