  background-color: #333;
}

.charge::before {
  content: "";
  display: inline-block;
  width: 10px;
  height: 2px;
  position: absolute;
  border-radius: 3px 3px 10px 10px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgb(26, 25, 25);
}

