  border-radius: 50%;
  position: absolute;
  top: -11px;
  left: 50%;
  transform: translateX(-50%);
}

.front-camera::after {
  content: "";
  position: absolute;
  border-radius: 50%;
  display: inline-block;
  top: 50%;
  left: 50%;
  width: 50%;
  height: 50%;
  transform: translate(-50%, -50%);
  background: radial-gradient(
    circle at 6px 2px,
    rgba(255, 255, 255, 0.726),
