/* From Uiverse.io by nanda248 - Tags: card, playing card, uno */
.card {
  width: 11em;
  height: 14.8em;
  position: relative;
  background: #28282B;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 6px solid #F5F5DC;
  border-radius: 5px;
  transition: 0.5s;
  overflow: hidden;
}

.card .circle {
  width: 8em;
  height: 14em;
  border-radius: 50%;
  transform: skew(-25deg);
  border: 4px solid white;
  display: grid;
  grid-template-columns: 1fr 1fr;
}

.card .circle.small {
  position: absolute;
  width: 1.5em;
  height: 3em;
  border: 1px solid white;
}

.card .circle.top-left {
  top: 0.3em;
  left: 0.6em;
}

.card .circle.bottom-right {
  bottom: 0.3em;
  right: 0.6em;
}

.card:hover {
  cursor: pointer;
  transform: translate(0, -5px);
}

.card:hover:after, .card:hover:before {
  visibility: hidden;
}

.card .circle span:nth-child(1) {
  background-color: #FF2400;
  border-top-left-radius: 100%;
}

.card .circle span:nth-child(2) {
  background-color: #1F51FF;
  border-top-right-radius: 100%;
}

.card .circle span:nth-child(3) {
  background-color: #FFEA00;
  border-bottom-left-radius: 100%;
}

.card .circle span:nth-child(4) {
  background-color: #50C878;
  border-bottom-right-radius: 100%;
}

/* glider animation */
.card:before {
  content: '';
  position: absolute;
  height: 130%;
  width: 1em;
  background-color: aliceblue;
  opacity: 0.14;
  animation: glider 1.8s infinite linear;
  animation-delay: 0.05s;
}

.card:after {
  content: '';
  position: absolute;
  height: 120%;
  width: 0.8em;
  background-color: aliceblue;
  opacity: 0.1;
  animation: glider 1.8s infinite linear;
}

@keyframes glider {
  0% {
    transform: rotate(45deg) translate(-12em, 1.2em);
  }

  100% {
    transform: rotate(45deg) translate(18em, -2.8em);
  }
}

/* From Uiverse.io by nanda248 - Tags: card, playing card, stack of cards, gold card */
.main {
  position: relative;
}

/* card styles */
.card {
  position: relative;
  overflow: hidden;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: 1fr 1fr 1fr;
  grid-auto-flow: row;
  grid-template-areas: "i1 . ."
    ". center ."
    ". . i2";
  width: 12em;
  height: 18em;
  border-radius: 4px;
  background: #ffd700;
  transition: 0.5s;
  z-index: 20;
}

.bg-card-1 {
  background-color: #daa520;
  position: absolute;
  top: 0;
  width: 12em;
  height: 18em;
  border-radius: 4px;
  transition: 0.5s;
  z-index: 8;
}

.bg-card-2 {
  background-color: #b8860b;
  position: absolute;
  top: 0;
  width: 12em;
  height: 18em;
  border-radius: 4px;
  transition: 0.5s;
  z-index: 4;
}

/* glider animation */
.card:before {
  content: '';
  position: absolute;
  height: 130%;
  width: 0.6em;
  background-color: aliceblue;
  opacity: 0.3;
  animation: glider 1.8s infinite linear;
  animation-delay: 0.05s;
}

.card:after {
  content: '';
  position: absolute;
  height: 130%;
  width: 0.4em;
  background-color: aliceblue;
  opacity: 0.2;
  animation: glider 1.8s infinite linear;
}

@keyframes glider {
  0% {
    transform: rotate(45deg) translate(-140px, -140px);
  }

  100% {
    transform: rotate(45deg) translate(220px, -40px);
  }
}

/* icon styles */
.i1,.i2 svg {
  width: 1.5em;
  fill: #FF3131;
}

.i1 {
  grid-area: i1;
  justify-self: flex-start;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  margin: 0.2em 0.4em;
}

.i2 {
  grid-area: i2;
  justify-self: flex-end;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  margin: 0.2em 0.4em;
  transform: rotate(180deg);
}

.corner-text {
  color: #FF3131;
  font-weight: bold;
  font-size: 1.2em;
}

.center-shape {
  grid-area: center;
  display: flex;
  justify-content: center;
  align-items: center;
}

.center-shape svg {
  width: 4em;
  fill: #FF3131;
}

/* hover effects */
.card:hover {
  box-shadow: #ffd700 0px 7px 29px 0px;
  cursor: pointer;
}

.card:hover:after, .card:hover:before {
  visibility: hidden;
}

.card:hover svg,
.card:hover span {
  animation: color-change 1s ease-in-out;
  animation-fill-mode: forwards;
}

@keyframes color-change {
  from {
    fill: #FF3131;
    color: #FF3131;
  }

  to {
    fill: #E34234;
    color: #E34234;
  }
}

/* hover fan out effects */
.card:active {
  transform: rotate(10deg);
  transform-origin: bottom left;
  box-shadow: none;
}

.card:active + .bg-card-1 {
  transform: rotate(5deg);
  transform-origin: bottom left;
}

