  height: 50%;
  display: block;
  background-color: rgba(var(--cardsquares), .5);
  filter: blur(10px);
  position: absolute;
  transition: all 5s ease-out;
  opacity: .1;
  z-index: 0;
}

.card:hover::after,
.card:hover::before {
  opacity: .5;
  rotate: 60deg;
}

.card:hover::after {
  translate: 100% 0;
}

.card:hover::before {
  translate: -100% 0;
}

.card .card-title {
  color: rgba(var(--cardText), .75);
  font-size: 1.1em;
  font-weight: 600;
  margin: .5em;
  position: relative;
  transition: all .3s ease-out;
  z-index: 100;
  text-shadow: 0px 0px 0px rgba(var(--cardText), .25);
}

.card:hover h3 {
  color: rgba(var(--cardText), 1);
  text-shadow: 0px 8px 5px rgba(var(--cardText), .35);
}

.card:hover span {
  filter: grayscale(0%);
}

/* Inner card */
.card-info::after {
  left: -6rem;
  bottom: 0;
}

.card-info::before {
  right: -6rem;
  top: 1rem;
}

/* Avatar container */
.card-info span {
  color: rgba(var(--cardBase), 1);
  display: flex;
  place-items: center;
  text-align: center;
  border-radius: 50%;
  overflow: hidden;
  background-color: rgba(var(--cardBase), .5);
  height: 80px;
  width: 50%;
  box-shadow: inset 0px 2px 4px rgba(var(--cardBase), .95),
  inset 0px 2px 40px rgba(var(--cardbg), .95);
  position: relative;
  transition: all .3s ease-out .1s;
  filter: grayscale(75%);
  z-index: 0;
}

.card-info span:hover .avatar {
  scale: 1.5;
}

.avatar {
  transition: all .3s ease-out;
  position: relative;
}

/* Inner card container and UI */
.card-info {
  --angle: 0deg;
  display: flex;
  flex-flow: column nowrap;
  place-items: center;
  padding: 1em;
  margin: 0 .75em;
  color: rgba(var(--cardText), 1);
  background-color: rgba(250, 246, 246, 1);
  transition: all .5s ease-out;
  animation: animateBorder 10s linear infinite reverse;
  border: .15em solid;
  position: relative;
  z-index: 10;
  border-image: linear-gradient(var(--angle), rgba(var(--cardbg), 1), rgba(var(--cardBase), .5), rgba(var(--cardbg), 1)) 1;
}

.card-info p {
  color: rgba(var(--cardText), 1);
  line-height: 1.25em;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  overflow: hidden;
  text-overflow: ellipsis;
  -webkit-line-clamp: 3;
  font-size: 1em;
  margin: 1em 0;
  transition: all .3s ease-out .3s;
}

.card-info .btn {
  display: block;
  padding: 8px 16px;
  background-color: rgba(var(--cardBase), .35);
  color: rgba(var(--cardTextBtn), .75);
  text-decoration: none;
  border-radius: 4px;
  font-size: .85em;
  transition: all 0.3s ease-in-out;
}

.card-info .btn:hover {
  cursor: pointer;
  background-color: rgba(var(--cardsquares), .35);
}

.card-info .btn:active {
  box-shadow: inset 0px -2px 3px rgba(var(--cardBase), .25);
}

/* Card links container */
.card-links {
  transition: all .3s linear;
  opacity: 0;
  display: flex;
  flex-flow: row wrap;
  gap: 1em;
  position: relative;
  top: -2rem;
  list-style: none;
  color: rgba(var(--cardText), 1);
  font-size: .75em
}

.card-links a {
  transition: color .3s ease-out;
}

.card-links a:hover {
  color: rgba(var(--cardBase), .75);
}

/* Card chckbox active state */
.card input[type="checkbox"] {
  visibility: hidden;
}

.card:hover:has(#toggle:checked) .card-title {
  text-shadow: 0px 2px 5px rgba(var(--cardText), .35);
}

.card:has(#toggle:checked) {
  border-radius: 10px;
  height: 295px;
}

.card:has(#toggle:checked) .card-info > *:not(.btn):not(span) {
  opacity: 0;
}

.card:has(#toggle:checked) .card-info {
  translate: 0 -20%;
  border-width: 1px;
  border: none;
  box-shadow: inset 0 0 5px rgba(var(--cardBase), .5),
  inset 0px 0px 2px rgba(var(--cardText), .25),
  inset 0px 30px 40px rgba(var(--cardBase), .25);
  border-radius: 10px;
}

.card:has(#toggle:checked) .card-info span {
  translate: 0 75%;
}

.card:has(#toggle:checked) .card-info p {
  transition: all .1s ease-out;
}

.card:has(#toggle:checked) .card-links {
  opacity: 1;
  top: -2rem;
}

.card #toggle {
  visibility: hidden;
}

@keyframes animateBorder {
