}

.one-div:after {
  content: '';
  position: absolute;
  left: 60px;
  top: 25px;
  width: 10px;
  height: 10px;
  border-radius: 42% 58% 37% 63% / 40% 43% 57% 60%;
  background: #ffffff
}

@keyframes anim {
  0% {
    transform: translateY(0);
    height: 150px;
    width: 150px;
