  opacity: 1;
}

.card::after {
  content: "";
  position: absolute;
  left: -2em;
  width: 5em;
  height: 175%;
  top: -15em;
  rotate: 45deg;
  opacity: 0;
  z-index: 1;
  transform: perspective(250px);
  transition: transform .7s ease-in-out;
  background: linear-gradient(to right, transparent 1%, rgba(255, 255, 255, 0.1) 40%,rgba(255, 255, 255, 0.1) 60% , transparent 100%);
}

.card:hover::after {
