/* From Uiverse.io by zakareaaljundi - Tags: card, flip */
.card {
  width: 190px;
  height: 254px;
  position: relative;
  border-radius: 40px;
  transition: all 0.8s;
  perspective: 600px;
  perspective-origin: center bottom;
}

.upper-part {
  width: 100%;
  height: 65%;
  border-radius: 40px 40px 0 0;
  position: relative;
  transform-style: preserve-3d;
  transition: all 0.9s;
}

.upper-part-face,
.upper-part-back {
  text-align: center;
  background-color: lightgray;
  color: purple;
  border: 3px solid purple;
  display: flex;
  justify-content: center;
  align-items: center;
  position: absolute;
  width: 100%;
  height: 100%;
  padding: 15px;
  border-radius: 40px 40px 0 0;
  font-weight: bold;
  z-index: 2;
  backface-visibility: hidden;
  transition: all 0.6s;
}

.upper-part-back {
  background-color: purple;
  color: lightgray;
  transform: rotatex(180deg);
}

.lower-part {
  width: 100%;
  height: 35%;
  border-radius: 0 0 40px 40px;
  position: relative;
  transform-style: preserve-3d;
  transform-origin: center top;
  transition: all 0.9s;
}

.lower-part-face,
.lower-part-back {
  background-color: purple;
  width: 100%;
  height: 100%;
  color: lightgray;
  font-weight: bold;
  position: absolute;
  border-radius: 0 0 40px 40px;
  display: flex;
  justify-content: center;
  align-items: center;
  transform: translate(0, -0.8px);
  backface-visibility: hidden;
  z-index: 2;
}

.lower-part-back {
  backface-visibility: visible;
  border-radius: 40px;
  color: purple;
  background-color: lightgray;
  transform: rotateX(180deg);
  z-index: 1;
  transition: border-radius 0.6s;
}

.card:hover > .upper-part {
  transform: rotatex(-0.5turn);
}

.card:hover > .lower-part {
  transform: translateY(88.3px) rotateX(0.5turn);
}

.card:hover > .lower-part > .lower-part-back {
  border: 3px solid purple;
  border-radius: 0 0 40px 40px;
}

/* From Uiverse.io by zanina-yassine  - Tags: blue, minimalist, loading, card, loader, gradients, multicolor, loading animation */
.card {
  width: 400px;
  max-width: 450px;
  height: fit-content;

  display: grid;
  grid-template-rows: auto 4px auto;

  padding-bottom: 20px;

  border-radius: 11px;

  background: #ffffff;
  box-shadow: 0px 187px 75px rgba(0, 0, 0, 0.01),
    0px 105px 63px rgba(0, 0, 0, 0.05), 0px 47px 47px rgba(0, 0, 0, 0.09),
    0px 12px 26px rgba(0, 0, 0, 0.1), 0px 0px 0px rgba(0, 0, 0, 0.1);
  overflow: hidden;
}

.card_title {
  display: block;

  padding: 12px 15px;

  color: rgb(70, 70, 76);
  font-size: 0.875rem;
  font-weight: 600;
  text-align: center;
}

.loader {
  height: 3px;
  width: 100%;

  position: relative;

  background-color: #e2e4e8;
  animation: retract 5s cubic-bezier(0.15, 0.83, 0.66, 1) 2s forwards infinite;
}

.card:has(.load-more:hover) .loader {
  height: 2px;
}

.loader::before {
  content: "";

  height: 100%;

  position: absolute;

  left: 0;

  background: linear-gradient(#4480ff 0%, #115dfc 50%, #0550ed 100%);
  animation: slide 5s cubic-bezier(0.15, 0.83, 0.66, 1) 2s forwards infinite;
}

.card_content {
  width: 100%;
  height: 150px;

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
}

.load-more {
  width: fit-content;
  height: 36px;

  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  gap: 10px;

  padding: 0 15px;

  border-radius: 5px;
  border: 0;

  background: linear-gradient(180deg, #4480ff 0%, #115dfc 50%, #0550ed 100%);
  font-style: normal;
  font-weight: 600;
  font-size: 12px;
  line-height: 15px;
  color: #ffffff;

  box-shadow: 0px 0.5px 0.5px #efefef, 0px 1px 0.5px rgba(239, 239, 239, 0.5);
  transition: all 0.6s cubic-bezier(0.15, 0.83, 0.66, 1);
  cursor: pointer;
}

.load-more:hover {
  transform: translateY(-1px);
  box-shadow: 0 10px 20px 0#054eed6b;
}

@keyframes slide {
  0% {
    width: 0%;
  }
  30% {
    width: 20%;
  }
  50% {
    width: 30%;
  }
  70% {
    width: 80%;
  }
  90% {
    width: 100%;
    opacity: 1;
  }
  100% {
    opacity: 0;
  }
}

@keyframes retract {
  0% {
    height: 3px;
  }
  95% {
    height: 3px;
  }
  100% {
    height: 1px;
  }
}

