  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  background: #fff;
  border-radius: 100%;
  animation: bounce2 3s ease-in-out infinite;
  transform-origin: bottom;
}

@keyframes bounce2 {
  0% {
    transform: scaleY(0.75) scaleX(1.25);
  }

  14.25% {
    transform: scaleY(1);
  }

