/* From Uiverse.io by ActiveIceDigital - Tags: notification */
@keyframes ud {
  0% {
    transform: translateY(-1px);
    opacity: 0;
  }

  50% {
    transform: translateY(-5px);
    opacity: 1;
  }

  100% {
    transform: translateY(-1px);
    opacity: 0;
  }
}

.notification {
  width: 250px;
  height: 60px;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 14px;
  padding: 5px 15px;
  text-align: center;
  box-shadow: 2px 2px 0px 1px rgba(255, 0, 0, 0.5) inset, 
  -2px -2px 0px 1px rgba(255, 0, 0, 0.8) inset,
  0px 2px 5px 0px rgba(0, 0, 0, .25);
}

#lvl {
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 2px;
  font-size: 20px;
}

.notification > span {
  font-size: 11px;
  text-transform: uppercase;
  letter-spacing: 1px;
}

#level {
  display: flex;
  justify-content: center;
  margin-top: 5px;
  gap: 5px;
}

.one {
  display: inline-block;
  font-size: 25px;
  line-height: 25px;
  animation: ud 1s ease-in-out infinite;
  color: rgba(255, 0, 0, 0.95);
}

/* From Uiverse.io by Fujitawa - Tags: notification */
.notification {
  width: 250px;
  height: 60px;
  color: black;
  background: white;
  border-radius: 3px;
  display: grid;
  grid-auto-flow: column;
  align-items: center;
  justify-items: center;
  -webkit-animation-name: pulse;
  animation-name: pulse;
  -webkit-animation-duration: 1s;
  animation-duration: 1s;
  -webkit-animation-fill-mode: both;
  animation-fill-mode: both;
  animation-iteration-count: infinite;
  border: 3px solid #ece0b4;
}

.notification:hover  button.ff3 {
  background-color: #f4e0a6;
}

.notification:hover {
  border-color: #f4e0a6;
}

.notification:hover .lvl {
  border-color: #f4e0a6;
}

button.ff3 {
  background-color: #ece0b4;
  height: 100%;
  display: flex;
  align-items: center;
  width: 100%;
  justify-content: center;
  flex-direction: column;
  gap: 3px;
  cursor: pointer;
  border: 0;
  transition: .5s;
  font-weight: bold;
}

.lvl {
  background: white;
  padding: 6px;
  border-radius: 100px;
  border: 3px solid #ece0b4;
  position: absolute;
  left: -10px;
  transform: scale(.8);
  top: -10px;
}

@-webkit-keyframes pulse {
  0% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }

  50% {
    -webkit-transform: scale3d(1.05, 1.05, 1.05);
    transform: scale3d(1.05, 1.05, 1.05);
  }

  100% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
}

@keyframes pulse {
  0% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }

  50% {
    -webkit-transform: scale3d(1.05, 1.05, 1.05);
    transform: scale3d(1.05, 1.05, 1.05);
  }

  100% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
}

/* From Uiverse.io by Gianluks90 - Tags: notification */
.notification {
  width: 255px;
  height: 60px;
  background: white;
  border-radius: 25px;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}

.icon {
  height: 40px;
  width: 40px;
  border-radius: 20px;
  background-color: rgb(236, 236, 236);
  display: flex;
  justify-content: center;
  align-items: center;
  padding-top: 2px;
}

.message-container {
  display: flex;
  flex-direction: column;
}

.title {
  padding: 2px;
  text-transform: uppercase;
  color: black;
  font-weight: 600;
}

.message {
  padding: 2px;
  color: black;
  font-size: small;
}

.gradient-border {
  --borderSize: 3px;
  position: relative;
  border-radius: var(--borderSize);
}

.gradient-border:after {
  content: '';
  position: absolute;
  top: calc(-1 * var(--borderSize));
  left: calc(-1 * var(--borderSize));
  height: calc(100% + var(--borderSize) * 2);
  width: calc(100% + var(--borderSize) * 2);
  background: linear-gradient(75deg, #E50000, #FF8D00, #FFEE00, #008121, #004CFF, #760188);
  border-radius: calc(2 * var(--borderSize));
  z-index: -1;
  animation: gradientAnimation 4s ease alternate infinite;
  background-size: 300% 300%;
}

@keyframes gradientAnimation {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

/* From Uiverse.io by JkHuger - Tags: notification */
.level-up-notification {
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: #FDD835;
  border-radius: 10px;
  font-weight: bold;
  font-family: "Courier New", monospace;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
  opacity: 0;
  animation: scale-up 1.5s ease-in-out forwards, fade-out 0.5s ease-in-out forwards 21.5s;
}

.level-up-text {
  text-align: center;
  color: #fff;
  font-size: 30px;
  padding: 12px 20px;
  font-weight: bold;
  text-shadow: 2px 2px #000;
}

@keyframes confetti {
  0% {
    transform: translateY(-50%);
  }

  100% {
    transform: translateY(100%);
  }
}

@keyframes scale-up {
  0% {
    transform: scale(0);
    opacity: 0;
  }

  100% {
    transform: scale(1);
    opacity: 1;
  }
}

.level-up-notification:hover {
  animation: none;
}

.level-up-notification:hover .confetti {
  animation: confetti 0.5s ease-in-out forwards;
}

/* asd */

.confetti-container {
  user-select: none;
  z-index: 10;
}

.confetti {
  position: absolute;
  left: 0;
  right: 0;
  display: flex;
  /* width: 600px; */
  /* height: 600px; */
  /* overflow: hidden; */
}

.confetti .square {
  width: 1rem;
  height: 1rem;
  background-color: var(--bg);
  transform: rotate(-140deg);
}

.confetti .rectangle {
  width: 1rem;
  height: 0.5rem;
  background-color: var(--bg);
}

.confetti .hexagram {
  width: 0;
  height: 0;
  border-left: 0.5rem solid transparent;
  border-right: 0.5rem solid transparent;
  border-bottom: 1rem solid var(--bg);
  position: relative;
}

.confetti .hexagram:after {
  content: "";
  width: 0;
  height: 0;
  border-left: 0.5rem solid transparent;
  border-right: 0.5rem solid transparent;
  border-top: 1rem solid var(--bg);
  position: absolute;
  top: 0.33rem;
  left: -0.5rem;
}

.confetti .pentagram {
  width: 0rem;
  height: 0rem;
  display: block;
  margin: 0.5rem 0;
  border-right: 1rem solid transparent;
  border-bottom: 0.7rem solid var(--bg);
  border-left: 1rem solid transparent;
  transform: rotate(35deg);
  position: relative;
}

.confetti .pentagram:before {
  content: "";
  width: 0;
  height: 0;
  display: block;
  border-bottom: 0.8rem solid var(--bg);
  border-left: 0.3rem solid transparent;
  border-right: 0.3rem solid transparent;
  transform: rotate(-35deg);
  position: absolute;
  top: -0.45rem;
  left: -0.65rem;
}

.confetti .pentagram:after {
  content: "";
  width: 0rem;
  height: 0rem;
  display: block;
  border-right: 1rem solid transparent;
  border-bottom: 0.7rem solid var(--bg);
  border-left: 1rem solid transparent;
  transform: rotate(-70deg);
  position: absolute;
  top: 0.03rem;
  left: -1.05rem;
}

.confetti .dodecagram {
  background: var(--bg);
  width: 0.8rem;
  height: 0.8rem;
  position: relative;
}

.confetti .dodecagram:before {
  content: "";
  height: 0.8rem;
  width: 0.8rem;
  background: var(--bg);
  transform: rotate(30deg);
  position: absolute;
  top: 0;
  left: 0;
}

.confetti .dodecagram:after {
  content: "";
  height: 0.8rem;
  width: 0.8rem;
  background: var(--bg);
  transform: rotate(60deg);
  position: absolute;
  top: 0;
  left: 0;
}

.confetti .wavy-line {
  position: relative;
}

.confetti .wavy-line::after,
.confetti .wavy-line::before {
  content: "";
  height: 1rem;
  width: 8rem;
  background-size: 2rem 1rem;
  position: absolute;
  left: -9rem;
  transform: rotate(90deg);
}

.confetti .wavy-line::before {
  background-image: linear-gradient(
    45deg,
    transparent,
    transparent 50%,
    var(--bg) 50%,
    transparent 60%
  );
  top: 1rem;
}

.confetti .wavy-line::after {
  background-image: linear-gradient(
    -45deg,
    transparent,
    transparent 50%,
    var(--bg) 50%,
    transparent 60%
  );
}

.confetti i {
  width: 3rem;
  height: 3rem;
  margin: 0 0.2rem;
  animation-name: confetti;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
  animation-duration: calc(70s / var(--speed));
}

.confetti i:nth-child(even) {
  transform: rotate(90deg);
}

@keyframes confetti {
  0% {
    transform: translateY(-500px);
  }

  100% {
    transform: translateY(500px);
  }
}

/* From Uiverse.io by John-CFO - Tags: notification */
.card {
  color: #090909;
  height: 12rem;
  width: 20rem;
  border-radius: 1rem;
  background: rgb(63, 94, 251);
  background: radial-gradient(
    circle,
    rgba(63, 94, 251, 1) 0%,
    rgba(252, 70, 223, 1) 100%
  );
  border: 1px solid #e8e8e8;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
}

.text-1 {
  margin: 0;
  font-family: fantasy;
  font-size: 35px;
  padding-bottom: 5px;
  color: transparent;
  -webkit-text-stroke: 1px #fff;
}

.text-2 {
  margin: 0;
  padding-bottom: 2rem;
  font-family: fantasy;
  font-size: 25px;
  color: #fff;
}

.text-1,
.text-2 {
  animation: scaling 5s infinite;
}

@keyframes scaling {
  0% {
    transform: scale(1);
    opacity: 1.8;
  }

  50% {
    transform: scale(1.1);
    opacity: 0.8;
  }
}

.metal {
  margin-top: 10px;
  width: 60px;
  animation: spin 5.4s cubic-bezier(0, 0.2, 0.8, 1) infinite;
}

@keyframes spin {
  0% {
    transform: rotateY(0deg);
  }

  100% {
    transform: rotateY(1800deg);
    animation-timing-function: cubic-bezier(0, 0.5, 0.5, 1);
  }
}

/* From Uiverse.io by M4NT - Tags: notification */
.notification {
  width: 250px;
  height: 60px;
  background: #18532c;
  border-radius: 14px;
  margin-top: 250px;
  user-select: none;
  -webkit-transform: translateY(0);
