.notification {
  width: 250px;
  height: 60px;
  background: rgb(255, 255, 255);
  border-radius: 14px;
  position: relative;
  border: 1px solid rgba(0, 0, 0, 0.377);
}

.shinner {
  position: absolute;
  width: 250px;
  height: 60px;
  overflow: hidden;
  top: 0;
  left: 0;
  border-radius: 0.5rem;
}

.mover {
  position: absolute;
  border-top: 60px solid rgba(255, 255, 255, 0.4);
  border-right: 30px solid transparent;
  left: 50px;
  filter: blur( 4px );
  animation: shine 2s infinite ease-in-out;
}

.mover2 {
  content: "";
  position: absolute;
  border-top: 60px solid rgba(255, 255, 255, 0.4);
  border-left: 30px solid transparent;
  transform: scaleY(-1);
  left: 20px;
  filter: blur( 4px );
  animation: shine2 2s infinite ease-in-out;
}

.levelup {
  color: rgb(18, 175, 4);
  text-align: center;
  padding-top: 0.6rem;
  font-weight: bolder;
  font-size: 1.8rem;
  z-index: 3;
}

.congrats {
  font-family: cursive;
  font-size: 1.5rem;
  color: rgb(240, 240, 240);
  text-align: center;
  padding-top: 1rem;
  text-shadow: 1px 4px 7px rgb(7, 7, 7);
}

.wing {
  transform: translate(150px,-100px);
  position: absolute;
  z-index: -1;
  animation: winganime 2s ease-in-out infinite;
}

.wing1 {
  transform: translate(-30px,-100px) scaleX(-1);
  position: absolute;
  z-index: -1;
  animation: winganime2 2s ease-in-out infinite;
}

.bg {
  background: rgb(223, 223, 105);
  width: 130px;
  height: 130px;
  position: absolute;
  top: 50%;
  right: 50%;
  transform: translate(50%,-50%);
  z-index: -2;
  border-radius: 50%;
  box-shadow: rgb(252, 255, 54,0.7) 0px 4px 105px, rgb(252, 255, 54,0.7) 0px -24px 130px, rgb(252, 255, 54,0.7) 0px 4px 446px, rgb(252, 255, 54,0.7) 0px 12px 13px, rgb(252, 255, 54,0.7) 0px -3px 5px;
  animation: rot 2.5s infinite ease-in-out;
  transform-origin: center;
}

@keyframes rot {
  0% {
    transform: translate(50%,-50%) rotate(0deg);
  }

  100% {
    transform: translate(50%,-50%) rotate(360deg);
  }
}

@keyframes winganime {
  0% {
    transform: translate(150px,-100px) rotateY(0deg);
  }

  50% {
    transform: translate(150px,-95px) rotateY(25deg);
  }

  100% {
    transform: translate(150px,-100px) rotateY(0deg);
  }
}

@keyframes winganime2 {
  0% {
    transform: translate(-30px,-100px) rotateY(0deg) scaleX(-1);
  }

  50% {
    transform: translate(-30px,-95px) rotateY(-25deg) scaleX(-1);
  }

  100% {
    transform: translate(-30px,-100px) rotateY(0deg) scaleX(-1);
  }
}

@keyframes shine {
  0% {
    left: -50px;
  }

  100% {
    left: 300px;
  }
}

@keyframes shine2 {
  0% {
    left: -20px;
  }

  100% {
    left: 270px;
  }
}

/* From Uiverse.io by Santaval - Tags: notification */
/*Container*/

.bannerContainer {
  position: relative;
  box-sizing: border-box;
  padding: 0;
  margin: 0;
  width: fit-content;
  height: fit-content;
  display: flex;
  justify-content: center;
  align-items: center;
}

/*Banner */

.bannerContainer .banner {
  background-color: #e7e5df;
  height: 70px;
  width: 250px;
  border-radius: 10px;
  position: relative;
  overflow-y: hidden;
}

/*Loaders */

.bannerContainer .banner .loader {
  height: 100%;
  width: 100%;
  border-radius: 10px;
  position: absolute;
}

.bannerContainer .banner .loader1 {
  background-color: #393e41;
  animation: loader 0.5s linear 0s;
}

.bannerContainer .banner .loader2 {
  background-color: #44bba4;
  animation: loader 0.5s linear 0.2s;
}

.bannerContainer .banner .loader3 {
  background-color: #e7bb41;
  animation: loader 0.5s linear 0.3s;
}

/*Main card*/

.bannerContainer .banner .mainCard {
  width: 100%;
  background-color: #e7e5df;
  display: flex;
  justify-content: space-around;
  align-items: center;
  color: #393e41;
  font-weight: bolder;
  animation: loader 0.5s linear 0.4s;
  border-radius: 10px;
}

.bannerContainer .banner span {
  font-size: 29px;
  font-weight: 900;
  transform: translateY(-120px);
  animation: appear 0.1s ease-in 0.9s forwards;
}

.bannerContainer .banner svg {
  width: 30px;
  transform: translateY(-100px);
  animation: appear 0.1s ease-in 0.9s forwards;
}

.bannerContainer .banner .mainCard:hover > .secondCard {
  height: 100%;
  color: #e7e5df;
}

/* Second card */

.bannerContainer .banner .secondCard {
  background-color: #393e41;
  display: flex;
  justify-content: space-around;
  align-items: center;
  color: transparent;
  height: 0px;
  transition: all 0.3s ease-in-out;
  cursor: pointer;
}

/* Animations */

@keyframes loader {
  from {
    width: 0%;
  }

  to {
    width: 100%;
  }
}

@keyframes appear {
  from {
    transform: translateY(-100px);
  }

  to {
    transform: translateY(0px);
  }
}

/* From Uiverse.io by StealthWorm - Tags: notification */
.notification {
  display: flex;
  flex-direction: column;
  position: relative;
  width: 300px;
  height: 200px;
  border-radius: 0.3rem;
  justify-content: center;
  align-items: center;
  background: rgb(0, 0, 0);
  transition: all 3s ease;
}

.text {
  opacity: 0;
  display: flex;
  flex-direction: column;
  position: absolute;
  width: 300px;
  height: 200px;
  justify-content: space-between;
  align-items: center;
  padding: 0.5rem;
  top: 1.5rem;
}

strong {
  background-size: 300% 300%;
  background-clip: text;
  -webkit-background-clip: text;
  color: transparent;
  background-image: linear-gradient(137.48deg, #ffdb3b 10%,#FE53BB 45%, #8F51EA 67%, #0044ff 87%);
  font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  text-shadow: 0 0 5px rgba(255, 255, 255, 0.438);
  animation: gradient 5s ease infinite;
}

.notification:hover .text {
  opacity: 1;
  transition: 1s;
  transition-delay: 3s;
  transform: translateY(-1.5rem);
}

.notification:hover .crystal {
  animation: pulse_crystal 1s ease;
  transition: 1s;
  transform: scale(1.3);
  box-shadow: 0 0 5px rgb(255, 255, 255);
  background: conic-gradient(#3fb2ff 0%, #261caf 45%, #44d2eb 50%, #fdfdfd 100%);
  clip-path: polygon(50% 0, 84% 30%, 84% 70%, 50% 100%, 15% 70%, 15% 30%);
}

.notification:hover .crystal-border {
  transition: 1s;
  clip-path: polygon(50% 0, 90% 30%, 90% 70%, 50% 100%, 10% 70%, 10% 30%);
}

.crystal-border {
  z-index: 3;
  display: flex;
  position: absolute;
  width: 45px;
  height: 45px;
  background: #000000;
  clip-path: polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%);
}

.crystal {
  inset: 12px;
  position: absolute;
  width: 20px;
  height: 20px;
  background: conic-gradient(#ca22b9 0%, #792e83 45%, #ff26e9 50%, #ffffff 100%);
  clip-path: polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%);
  transition: all 1s ease;
}

.line-center-border {
  z-index: 2;
  display: flex;
  position: absolute;
  width: 10rem;
  height: 10rem;
  align-items: center;
  justify-content: center;
  background: #000000;
  clip-path: polygon(50% 20%, 60% 18%, 60% 80%, 50% 90%, 40% 80%, 40% 18%);
}

.line-center {
  display: flex;
  position: absolute;
  margin: 4px;
  width: 3rem;
  top: 1.8rem;
  height: 7.7rem;
  transition: 2s;
  opacity: 0;
  background: linear-gradient(90deg, #796f1c 50%, #c7b82f 50%);
  clip-path: polygon(50% 5%, 60% 8%, 60% 87%, 50% 90%, 40% 87%, 40% 8%);
}

.notification:hover .line-center {
  z-index: 1;
  opacity: 1;
}

.progress-ring {
  z-index: 2;
  position: absolute;
  top: 22%;
  stroke-dasharray: 135 120%;
  stroke: #113736;
  border-radius: 50%;
  transform: rotate(-12deg);
  transition: all 4s ease;
}

.notification:hover .progress-ring__blue {
  transition: all 2s ease;
  stroke-dasharray: 13 5;
}

.progress-ring-m7 {
  z-index: 1;
  visibility: hidden;
  width: 7rem;
  height: 5.5rem;
  top: 28%;
  background: transparent;
  position: absolute;
  border-radius: 50%;
  border-style: double;
  border: 8px solid #b9a80f;
  border-bottom-color: transparent;
  border-top-color: transparent;
}

.progress-ring-m7-inner {
  z-index: 1;
  visibility: hidden;
  display: flex;
  box-sizing: content-box;
  width: 6rem;
  height: 4.5rem;
  background: transparent;
  position: absolute;
  border-radius: 50%;
  border-style: dashed;
  border: 6px dashed #113736;
  border-bottom-color: transparent;
  border-top-color: transparent;
}

.notification:hover .progress-ring-m7 {
  visibility: visible;
  animation: pulse_m7 1.5s ease-in-out;
}

.notification:hover .progress-ring-m7-inner {
  visibility: visible;
  transition: all 3s ease;
  animation: pulse_m7 1.5s ease-in-out;
}

.progress-ring-two:nth-of-type(2) {
  z-index: 1;
  position: absolute;
  top: 20%;
  stroke-dasharray: 140 100;
  stroke: #b9a80f;
  border-radius: 50%;
  transform: rotate(27deg);
}

.progress-ring-two:nth-of-type(3) {
  z-index: 1;
  position: absolute;
  top: 22%;
  stroke-dasharray: 140 100;
  stroke: #b9a80f;
  border-radius: 50%;
  transform: rotate(-20deg);
}

.notification:hover svg {
  animation: pulse_svg 3s ease;
}

.wings {
  z-index: 1;
  position: absolute;
  top: 20%;
  width: 10rem;
  height: 8rem;
  clip-path: polygon(
  0% 0%, 28% 27%, 32% 31%, 27% 40%, 25% 50%, 
  30% 60%, 47% 67%, 45% 67%, 57% 66%, 64% 64%, 
  70% 59%, 70% 46%, 70% 46%, 75% 40%, 

  70% 26%, 100% 0%, 100% 10%, 95% 14%,

  95% 19%, 90% 23%, 90% 29%, 69% 50%, 
  72% 56%, 69.38% 62%, 65% 65%, 57.5% 66%, 50% 67%, 41% 67%, 
  32% 64%, 

  28% 49%, 10% 30%, 10% 22%, 5% 18%, 5% 13%, 0% 9%
  );
  background-color: #b9a80f;
  transition: all px2s ease;
}

.notification:hover .wings {
  transform: scale(1.2);
  animation: pulse_wing 3s ease;
}

.notification:hover .shield {
  animation: pulse 2s ease;
}

.notification:hover .shield-bottom-left {
  left: 3.5rem;
}

.notification:hover .shield-bottom-right {
  left: 6.2rem;
}

.shield {
  z-index: 4;
  display: flex;
  position: absolute;
  width: fit-content;
  top: 19%;
  left: 17%;
  flex-direction: column;
  gap: 3rem;
  transition: all 2s ease;
}

.shield-up, 
.shield-down {
  display: flex;
  position: relative;
}

.shield-bottom-left {
  z-index: 1;
  display: flex;
  position: absolute;
  width: 3.4rem;
  height: 5rem;
  left: 4rem;
  clip-path: polygon(22% 12%, 60% 36%, 60% 90%, 22% 64%);
  background-color: #696115;
}

.shield-bottom-right {
  z-index: 1;
  display: flex;
  position: absolute;
  width: 3.4rem;
  height: 5rem;
  left: 5.7rem;
  clip-path: polygon(62% 12%, 20% 36%, 20% 90%, 62% 64%);
  background-color: #696115;
}

.shield-bottom-right-inside {
  clip-path: polygon(62% 12%, 20% 36%, 20% 90%, 62% 64%);
}

.shield-bottom-left-inside {
  clip-path: polygon(22% 12%, 60% 36%, 60% 90%, 22% 64%);
}

.shield-bottom-left-inside,
.shield-bottom-right-inside {
  position: absolute;
  background-color: #b9a80f;
  inset: 5px;
}

.shield-top-left {
  display: flex;
  position: absolute;
  width: 3.4rem;
  height: 4rem;
  left: 4rem;
  clip-path: polygon(1% 10%, 10% 0%, 58% 20%, 58% 72%, 40% 86%, 18% 70%, 18% 74%, 22% 65%, 25% 55%, 22% 41%, 18% 31%, 10% 20%);
  background-color: #696115;
}

.shield-top-right {
  display: flex;
  position: absolute;
  width: 3.4rem;
  height: 4rem;
  left: 5rem;
  clip-path: polygon(100% 10%, 90% 0%, 42% 20%, 42% 70%, 60% 85%, 80% 70%, 76% 60%, 75% 55%, 75% 55%, 78% 41%, 82% 31%, 90% 20%);
  background-color: #696115;
}

.notification:hover .shield-top-left {
  left: 3.6rem;
  clip-path: polygon(1% 10%, 10% 0%, 58% 20%, 58% 70%, 45% 78% , 45% 95%, 15% 70%, 15% 68%, 22% 66%, 25% 55%, 22% 41%, 18% 31%, 10% 20%);
}

.notification:hover .shield-top-right {
  left: 5.4rem;
  clip-path: polygon(100% 10%, 90% 0%, 42% 20%, 42% 70%, 55% 78%, 55% 95%, 84% 75%, 78% 68%, 79% 69%, 75% 55%, 78% 41%, 82% 31%, 90% 20%);
}

.notification:hover .shield-top-right-inside {
  clip-path: polygon(100% 10%, 90% 0%, 42% 20%, 42% 70%, 55% 78%, 55% 95%, 84% 75%, 78% 68%, 79% 69%, 75% 55%, 78% 41%, 82% 31%, 90% 20%);
}

.notification:hover .shield-top-left-inside {
  clip-path: polygon(1% 10%, 10% 0%, 58% 20%, 58% 70%, 45% 78% , 45% 95%, 15% 70%, 15% 68%, 22% 66%, 25% 55%, 22% 41%, 18% 31%, 10% 20%);
}

.shield-top-right-inside {
  clip-path: polygon(100% 10%, 90% 0%, 42% 20%, 42% 70%, 60% 85%, 80% 70%, 76% 60%, 75% 55%, 75% 55%, 78% 41%, 82% 31%, 90% 20%);
}

.shield-top-left-inside {
  clip-path: polygon(1% 10%, 10% 0%, 58% 20%, 58% 72%, 40% 86%, 18% 70%, 18% 74%, 22% 65%, 25% 55%, 22% 41%, 18% 31%, 10% 20%);
}

.shield-top-left-inside,
.shield-top-right-inside {
  position: absolute;
  background-color: #b9a80f;
  inset: 4px;
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }

  70% {
    transform: scale(1.3);
    left: 7%;
    filter: brightness(150%);
  }

  100% {
    transform: scale(1);
  }
}

@keyframes pulse_wing {
  0% {
    transform: scale(1);
  }

  70% {
    transform: scale(1.3);
    filter: brightness(150%);
  }

  100% {
    transform: scale(1.2);
    filter: brightness(100%);
  }
}

@keyframes pulse_svg {
  0% {
    filter: brightness(100%);
  }

  70% {
    filter: brightness(150%);
  }

  100% {
    filter: brightness(100%);
  }
}

@keyframes pulse_m7 {
  0% {
    filter: brightness(100%);
    visibility: hidden;
    transform: scale(1);
  }

  70% {
    filter: brightness(150%);
    visibility: visible;
    transform: scale(1.3);
  }

  100% {
    filter: brightness(100%);
    visibility: visible;
    transform: scale(1);
  }
}

@keyframes pulse_crystal {
  0% {
    transform: scale(1);
    transform: rotateY(3rad);
  }

  70% {
    transform: scale(1.5);
    filter: brightness(180%);
    transform: rotateY(10rad);
  }

  100% {
    transform: scale(1.3);
    filter: brightness(100%);
    transform: rotateY(80rad);
  }
}

@keyframes show {
  0% {
    visibility: hidden;
    transform: translateY(0);
  }

  100% {
    visibility: visible;
    transform: translateY(5rem);
  }
}

@keyframes gradient {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

/* From Uiverse.io by WittyHydra - Tags: notification */
.notification {
  --bg-color: #fff;
  --text-color: #1c1f2b;
  --highlight-color: #fc9a32;
  --button-color: #3d3f4e;
  --button-color-hover: #27282f;
  position: relative;
  width: 300px;
  height: 100px;
  background-color: var(--bg-color);
  border-radius: 10px;
  -webkit-box-shadow: 0 8px 24px rgba(0, 0, 0, 0.2);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.2);
  overflow: hidden;
}

@media (prefers-color-scheme: dark) {
  .notification {
    --bg-color: #1c1f2b;
    --text-color: #fff;
    --highlight-color: #fc9a32;
    --button-color: #3d3f4e;
    --button-color-hover: #27282f;
  }
}

.notification-top {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 50%;
  background-color: var(--highlight-color);
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-box-pack: center;
  -ms-flex-pack: center;
  justify-content: center;
  -webkit-transform: translateY(-100%);
  -ms-transform: translateY(-100%);
  transform: translateY(-100%);
  -webkit-animation: slide-down 1s ease-out forwards;
  animation: slide-down 1s ease-out forwards;
}

.notification-bottom {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 50%;
  background-color: var(--bg-color);
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-box-pack: justify;
  -ms-flex-pack: justify;
  justify-content: space-between;
  padding: 20px;
}

.level-up {
  color: var(--text-color);
  font-size: 24px;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 2px;
  -webkit-animation: rotate-text 1s ease-in-out;
  animation: rotate-text 1s ease-in-out;
}

.level {
  color: var(--text-color);
  font-size: 20px;
  font-weight: bold;
  margin-right: 10px;
}

.next-level-button {
  background-color: var(--button-color);
  color: var(--text-color);
  border: none;
  border-radius: 5px;
  padding: 8px 16px;
  cursor: pointer;
  -webkit-transition: background-color 0.3s ease-out;
  transition: background-color 0.3s ease-out;
}

.next-level-button {
  background-color: #efefef;
  color: #000;
  font-weight: 600;
  padding: 5px 10px;
  border: none;
  border-radius: 5px;
  -webkit-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
  -webkit-box-shadow: 0px 0px 10px #888888;
  box-shadow: 0px 0px 10px #888888;
}

@-webkit-keyframes slide-down {
  from {
    -webkit-transform: translateY(-100%);
    transform: translateY(-100%);
  }

  to {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }
}

@keyframes slide-down {
  from {
    -webkit-transform: translateY(-100%);
    transform: translateY(-100%);
  }

  to {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }
}

@-webkit-keyframes rotate-text {
  0% {
    -webkit-transform: rotateX(90deg);
    transform: rotateX(90deg);
    opacity: 0;
  }

  100% {
    -webkit-transform: rotateX(0);
    transform: rotateX(0);
    opacity: 1;
  }
}

@keyframes rotate-text {
  0% {
    -webkit-transform: rotateX(90deg);
    transform: rotateX(90deg);
    opacity: 0;
  }

  100% {
    -webkit-transform: rotateX(0);
    transform: rotateX(0);
    opacity: 1;
  }
}

.next-level-button:hover {
  background-color: aqua;
  color: #000;
  -webkit-transform: scale(1.1);
  -ms-transform: scale(1.1);
  transform: scale(1.1);
  -webkit-box-shadow: 0px 0px 15px #888888;
  box-shadow: 0px 0px 15px #888888;
}

