/* From Uiverse.io by csemszepp - Source: https://codepen.io/t_afif/pen/PoEebrz - Tags: switch, toggle, toggle switch */
.togglesw {
  --s: 50px;
 /* adjust this to control the size*/
  height: calc(var(--s) + var(--s)/5);
  width: auto;
 /* some browsers need this */
  aspect-ratio: 2.25;
  border-radius: var(--s);
  margin: calc(var(--s)/2);
  display: grid;
  cursor: pointer;
  background-color: #ff7a7a;
  box-sizing: content-box;
  overflow: hidden;
  transition: .3s .1s;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
}

.togglesw:before {
  content: "";
  padding: calc(var(--s)/10);
  --_g: radial-gradient(circle closest-side at calc(100% - var(--s)/2) 50%,#000 96%,#0000);
  background: var(--_g) 0 /var(--_p,var(--s)) 100% no-repeat content-box,
    var(--_g) var(--_p,0)/var(--s)  100% no-repeat content-box,
    #fff;
  mix-blend-mode: darken;
  filter: blur(calc(var(--s)/12)) contrast(11);
  transition: .4s, background-position .4s .1s,
    padding cubic-bezier(0,calc(var(--_i,-1)*200),1,calc(var(--_i,-1)*200)) .25s .1s;
}

.togglesw:checked {
  background-color: #85ff7a;
}

.togglesw:checked:before {
  padding: calc(var(--s)/10 + .05px) calc(var(--s)/10);
  --_p: 100%;
  --_i: 1;
}

/* From Uiverse.io by csemszepp - Source: codepen.io/narohan/pen/dyNwjJL - Tags: switch, checkbox, cool checkbox */
.switch {
  position: relative;
  display: inline-block;
  width: 80px;
  height: 80px;
  border: 2px solid #dcdcdc;
  background: #e0e0e0;
  box-shadow: 7px 7px 23px #bebebe, -7px -7px 23px #ffffff;
  overflow: hidden;
  border-radius: 60px;
}

.switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  -webkit-transition: 0.5s;
  transition: 0.5s;
}

.chbox:checked + .slider:before {
  background: white;
  box-shadow: none;
}

.chbox:focus + .slider {
  box-shadow: 0 0 1px #2196f3;
}

.slider {
  color: #9a9a9a;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 11px;
  font-family: sans-serif;
}

.slider--0 {
  color: white;
  font-weight: 600;
  background-color: #49d84e;
}

.slider--1 div {
  transition: 0.5s;
}

.slider--1 div {
  position: absolute;
  width: 100%;
  height: 50%;
  left: 0;
}

.chbox:checked ~ .slider--1 div:first-child {
  transform: translateY(-100%);
  transition-delay: 1s;
}

.chbox:checked ~ .slider--1 div:last-child {
  transform: translateY(100%);
  transition-delay: 1s;
}

.chbox:checked ~ .slider--2 {
  transform: translateX(100%);
  transition-delay: 0.5s;
}

.chbox:checked ~ .slider--3 {
  transform: translateX(-100%);
  transition-delay: 0s;
}

.slider--1 div:first-child {
  transform: translateY(0);
  top: 0;
  background-color: #f3f3f3;
  transition-delay: 0s;
}

.slider--1 div:last-child {
  transform: translateY(0);
  bottom: 0;
  background-color: #f3f3f3;
  border-top: 1px solid #e0e0e0;
  transition-delay: 0s;
}

.slider--2 {
  background-color: #e6e6e6;
  transition-delay: 0.5s;
  transform: translateX(0);
  border-left: 1px solid #d2d2d2;
}

.slider--3 {
  background-color: #d2d2d2;
  transition-delay: 1s;
  transform: translatex(0);
  border-right: 1px solid #d2d2d2;
}

