  bottom: calc(50% - 4px/ 2);
}

#checkbox:checked + .toggle .bar--middle {
  opacity: 0;
  transform: rotate(-135deg);
  transition-delay: calc(0s + 0.35s * .3);
}

#checkbox:checked + .toggle .bar--bottom {
  top: calc(50% - 4px/ 2);
  transform: rotate(-225deg);
  transition-delay: calc(0s + 0.35s * .6);
}

/* From Uiverse.io by zanina-yassine - Tags: switch, ios */
/* Remove this container when use*/
.component-title {
  width: 100%;
  position: absolute;
  z-index: 999;
  top: 30px;
  left: 0;
  padding: 0;
  margin: 0;
  font-size: 1rem;
  font-weight: 700;
  color: #888;
  text-align: center;
}

/* The switch - the box around the slider */
.container {
  width: 51px;
  height: 31px;
  position: relative;
}

/* Hide default HTML checkbox */
.checkbox {
  opacity: 0;
  width: 0;
  height: 0;
  position: absolute;
}

.switch {
  width: 100%;
  height: 100%;
  display: block;
  background-color: #e9e9eb;
  border-radius: 16px;
  cursor: pointer;
  transition: all 0.2s ease-out;
}

/* The slider */
.slider {
  width: 27px;
  height: 27px;
  position: absolute;
  left: calc(50% - 27px/2 - 10px);
  top: calc(50% - 27px/2);
  border-radius: 50%;
  background: #FFFFFF;
  box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.15), 0px 3px 1px rgba(0, 0, 0, 0.06);
  transition: all 0.2s ease-out;
  cursor: pointer;
}

.checkbox:checked + .switch {
  background-color: #34C759;
}

.checkbox:checked + .switch .slider {
  left: calc(50% - 27px/2 + 10px);
  top: calc(50% - 27px/2);
}

/* From Uiverse.io by zebra76966 - Tags: switch, moon, sun, toggle, light&dark, emoji */
.switch {
  position: absolute;
  top: 50%;
  width: 30px;
  height: 60px;
  padding: 2px;
  left: 50%;
  transform: translate(-50%,-50%);
}

.switch input {
  opacity: 0;
  height: 0;
  width: 0;
}

.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  border-radius: 20px;
  border: 1px solid rgba(0,0,0,0.2);
  border-style: outset;
  background-color: white;
  transition: 0.4s;
}

.slider::before {
  content: "🌞";
  font-size: 22px;
  line-height: 15px;
  border-radius: 100%;
  position: absolute;
  left: -0.8px;
  top: 8px;
  background-color: transparent;
  transition: 0.4s;
}

.switch > input:checked + .slider {
  background-color: #000429;
}

.switch > input:checked + .slider:before {
  content: "🌑";
  font-size: 22px;
  line-height: 25px;
  background-color: transparent;
  transform: translateY(24px);
}
