/* From Uiverse.io by SmookyDev  - Tags: simple, neumorphism, skeuomorphism, flashy, tooltip, icon, hover effect, css */
.tooltip {
  position: relative;
  display: inline-block;
  cursor: pointer;
  font-family: "Arial", sans-serif;
}

.tooltip:hover .tooltiptext {
  visibility: visible;
  opacity: 1;
}

.tooltiptext {
  visibility: hidden;
  width: 200px;
  background-color: #333;
  color: #fff;
  text-align: center;
  border-radius: 5px;
  padding: 10px;
  position: absolute;
  z-index: 1;
  top: 125%;
  left: 50%;
  margin-left: -100px;
  opacity: 0;
  transition: opacity 0.3s;
}

.tooltiptext::after {
  content: "";
  position: absolute;
  top: -10px;
  left: 50%;
  margin-left: -10px;
  border-width: 10px;
  border-style: solid;
  border-color: transparent transparent #333 transparent;
}

.tooltip .icon {
  display: inline-block;
  width: 20px;
  height: 20px;
  background-color: #4caf50;
  color: #fff;
  border-radius: 50%;
  text-align: center;
  line-height: 20px;
}

/* From Uiverse.io by SouravBandyopadhyay  - Tags: simple, tooltip, animation, text animation, animated, transition, hover effect, hover button */
.payment-button {
  --background: #007bff;
  --text-color: #fff;
  --hover-background: #0056b3;
  --hover-text-color: #fff;
  --border-radius: 4px;
  --box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);

  display: inline-block;
  cursor: pointer;
  font-size: 18px;
  padding: 0.5em 1.5em;
  background: var(--background);
  color: var(--text-color);
  border: none;
  border-radius: var(--border-radius);
  box-shadow: var(--box-shadow);
  transition: transform 0.2s ease-in-out, background 0.3s ease-in-out,
    color 0.3s ease-in-out, box-shadow 0.3s ease-in-out;
  text-transform: uppercase;
  font-weight: 600;
  position: relative;
  overflow: hidden;
}

.label {
  z-index: 1;
}

.price {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: grid;
  place-items: center;
  transform-origin: 100%;
  transform: scale(1);
  transition: transform 0.3s ease-in-out, color 0.3s ease-in-out,
    background 0.3s ease-in-out;
  color: var(--background);
  background: var(--text-color);
  border-radius: var(--border-radius);
  font-weight: bold;
  font-size: 14px;
  overflow: hidden;
}

